import { PrettyChatWindow } from 'react-chat-engine-pretty';
import React from 'react';
import Message from './Message/Message'; // Import the Message component

const ChatsPage = (props) => {
  console.log("In ChatsPage")
  return (
    <div className="background">
      <div className="chat-wrapper">
        <PrettyChatWindow
          projectId={import.meta.env.VITE_CHAT_ENGINE_PROJECT_ID}
          username={props.user.username}
          secret={props.user.secret}
          renderMessageBubble={(creds, chat, lastMessage, message, nextMessage) => (
            <Message creds={creds} chat={chat} lastMessage={lastMessage} message={message} nextMessage={nextMessage} />
          )}
        />
      </div>
    </div>
  );
};

export default ChatsPage;
